// frontend/src/components/Admin/Approvals.js
import React, { useState, useEffect, useCallback } from 'react';
import { getPendingApprovals, approvePayment, rejectPayment } from '../../api';

const Approvals = () => {
  const [pendingPayments, setPendingPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const fetchPendingPayments = useCallback(async () => {
    try {
      setLoading(true);
      const response = await getPendingApprovals();
      setPendingPayments(response.payments || []);
      setError('');
    } catch (error) {
      console.error('Error fetching pending payments:', error);
      setError('Failed to fetch pending payments');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchPendingPayments();
  }, [fetchPendingPayments]); // ✅ Fixed dependency

  const handleApproval = async (transactionId, action, notes = '') => {
    try {
      if (action === 'approve') {
        await approvePayment(transactionId, action, notes);
      } else {
        await rejectPayment(transactionId, notes);
      }
      fetchPendingPayments(); // Refresh list
    } catch (error) {
      console.error('Error processing approval:', error);
      setError('Failed to process approval');
    }
  };

  if (loading) return <div>Loading pending payments...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <h2>Payment Approvals</h2>
      {pendingPayments.length === 0 ? (
        <p>No pending payments</p>
      ) : (
        pendingPayments.map(payment => (
          <div key={payment.id} style={{ 
            border: '1px solid #ddd', 
            padding: '10px', 
            margin: '10px 0',
            borderRadius: '4px'
          }}>
            <h4>{payment.memberName}</h4>
            <p>Amount: Rp {payment.amount?.toLocaleString()}</p>
            <p>Date: {new Date(payment.createdAt).toLocaleDateString()}</p>
            <button 
              onClick={() => handleApproval(payment.id, 'approve')}
              style={{ backgroundColor: '#28a745', color: 'white', border: 'none', padding: '5px 10px', marginRight: '10px' }}
            >
              Approve
            </button>
            <button 
              onClick={() => handleApproval(payment.id, 'reject')}
              style={{ backgroundColor: '#dc3545', color: 'white', border: 'none', padding: '5px 10px' }}
            >
              Reject
            </button>
          </div>
        ))
      )}
    </div>
  );
};

export default Approvals;
