import React, { useState, useEffect } from 'react';
import { getPaymentHistory } from '../../api';
import LoadingSpinner from '../Common/LoadingSpinner';
import './History.css';

const History = () => {
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState({
    year: new Date().getFullYear(),
    status: 'all'
  });

  useEffect(() => {
    fetchHistory();
  }, [filter]);

  const fetchHistory = async () => {
    try {
      setLoading(true);
      const response = await getPaymentHistory(filter);
      setHistory(response.data || []);
    } catch (error) {
      console.error('Failed to fetch payment history:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilter(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const getStatusBadge = (status) => {
    const statusClasses = {
      approved: 'success',
      pending: 'warning',
      rejected: 'danger'
    };
    
    return (
      <span className={`status-badge ${statusClasses[status.toLowerCase()]}`}>
        {status}
      </span>
    );
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('id-ID', {
      day: '2-digit',
      month: 'short',
      year: 'numeric'
    });
  };

  if (loading) return <LoadingSpinner />;

  return (
    <div className="payment-history">
      <div className="page-header">
        <h2>Payment History</h2>
        <p>View your complete payment transaction history</p>
      </div>

      <div className="history-filters">
        <div className="filter-group">
          <label htmlFor="year">Year:</label>
          <select
            id="year"
            name="year"
            value={filter.year}
            onChange={handleFilterChange}
          >
            {Array.from({ length: 5 }, (_, i) => {
              const year = new Date().getFullYear() - i;
              return (
                <option key={year} value={year}>{year}</option>
              );
            })}
          </select>
        </div>

        <div className="filter-group">
          <label htmlFor="status">Status:</label>
          <select
            id="status"
            name="status"
            value={filter.status}
            onChange={handleFilterChange}
          >
            <option value="all">All Status</option>
            <option value="approved">Approved</option>
            <option value="pending">Pending</option>
            <option value="rejected">Rejected</option>
          </select>
        </div>

        <button className="refresh-btn" onClick={fetchHistory}>
          ðŸ”„ Refresh
        </button>
      </div>

      <div className="history-summary">
        <div className="summary-card">
          <h3>Total Payments</h3>
          <p className="summary-value">{history.length}</p>
        </div>
        <div className="summary-card">
          <h3>Total Amount</h3>
          <p className="summary-value">
            Rp {history
              .filter(h => h.status.toLowerCase() === 'approved')
              .reduce((sum, h) => sum + parseFloat(h.amount || 0), 0)
              .toLocaleString()}
          </p>
        </div>
        <div className="summary-card">
          <h3>Pending</h3>
          <p className="summary-value">
            {history.filter(h => h.status.toLowerCase() === 'pending').length}
          </p>
        </div>
      </div>

      <div className="history-table-container">
        {history.length > 0 ? (
          <table className="history-table">
            <thead>
              <tr>
                <th>Date</th>
                <th>Month/Year</th>
                <th>Amount</th>
                <th>Status</th>
                <th>Description</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {history.map((payment) => (
                <tr key={payment.id}>
                  <td>{formatDate(payment.created_at)}</td>
                  <td>{payment.month}/{payment.year}</td>
                  <td className="amount">Rp {parseFloat(payment.amount || 0).toLocaleString()}</td>
                  <td>{getStatusBadge(payment.status)}</td>
                  <td>{payment.description || '-'}</td>
                  <td>
                    <div className="action-buttons">
                      {payment.receipt_url && (
                        <button
                          className="btn-view"
                          onClick={() => window.open(payment.receipt_url, '_blank')}
                        >
                          View Receipt
                        </button>
                      )}
                      {payment.status.toLowerCase() === 'pending' && (
                        <button className="btn-edit">
                          Edit
                        </button>
                      )}
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <div className="no-data">
            <p>No payment history found for the selected filters.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default History;
