import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';
import api from '../../api';
import './Login.css';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);

  const { login: authLogin } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    console.log(`📝 Form field changed: ${name} = "${value}"`);
    
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    if (errors.general) {
      setErrors({});
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrors({});
    setIsLoading(true);

    // Debug: Log form data before sending
    console.log('📊 Form data before submit:', formData);
    console.log('📊 Email length:', formData.email.length);
    console.log('📊 Password length:', formData.password.length);

    // Frontend validation
    if (!formData.email || !formData.password) {
      console.error('❌ Frontend validation failed: Empty fields');
      setErrors({ general: 'Please fill in both email and password' });
      setIsLoading(false);
      return;
    }

    try {
      console.log('🚀 Sending login request...');
      
      // Direct API call untuk debugging
      const response = await api.post('/auth/login', {
        email: formData.email.trim(),
        password: formData.password.trim()
      });

      console.log('✅ Login response received:', response.data);

      if (response.data.success) {
        console.log('🎉 Login successful! Redirecting...');
        
        // Store user data in localStorage
        localStorage.setItem('user', JSON.stringify(response.data.user));
        
        const targetPath = response.data.user.role === 'admin' 
          ? '/admin/dashboard' 
          : '/member/dashboard';
        
        // Use navigate instead of window.location.href
        navigate(targetPath, { replace: true });
        
      } else {
        console.error('❌ Login failed:', response.data.message);
        setErrors({ general: response.data.message });
      }

    } catch (error) {
      console.error('❌ Login error:', error);
      
      const errorMessage = error.response?.data?.message || 'Network error. Please try again.';
      setErrors({ general: errorMessage });
    } finally {
      setIsLoading(false);
    }
  };

  const handleQuickLogin = async (role) => {
    setIsLoading(true);
    
    const credentials = {
      admin: { email: 'admin@himakeu.com', password: 'admin123' },
      member: { email: 'member@himakeu.com', password: 'member123' }
    };

    console.log('🚀 Quick login attempt:', credentials[role]);

    try {
      const response = await api.post('/auth/login', credentials[role]);
      
      if (response.data.success) {
        const targetPath = role === 'admin' ? '/admin/dashboard' : '/member/dashboard';
        console.log('✅ Quick login successful, redirecting to:', targetPath);
        window.location.href = targetPath;
      } else {
        setErrors({ general: `Quick login failed: ${response.data.message}` });
      }
    } catch (error) {
      console.error('❌ Quick login error:', error);
      setErrors({ general: 'Quick login failed' });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-form">
        <h1>HIMAKEU Finance</h1>
        <p>Sign in to your account</p>

        {/* Quick Login Buttons */}
        <div className="quick-login">
          <p>Quick Login for Testing:</p>
          <button 
            type="button"
            onClick={() => handleQuickLogin('admin')}
            disabled={isLoading}
            style={{
              backgroundColor: '#007bff',
              color: 'white',
              padding: '10px 20px',
              margin: '5px',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            {isLoading ? 'Loading...' : 'Login as Admin'}
          </button>
          <button 
            type="button"
            onClick={() => handleQuickLogin('member')}
            disabled={isLoading}
            style={{
              backgroundColor: '#28a745',
              color: 'white',
              padding: '10px 20px',
              margin: '5px',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            {isLoading ? 'Loading...' : 'Login as Member'}
          </button>
        </div>

        {/* Error Message */}
        {errors.general && (
          <div style={{
            backgroundColor: '#f8d7da',
            color: '#721c24',
            padding: '10px',
            borderRadius: '4px',
            marginBottom: '15px',
            border: '1px solid #f5c6cb'
          }}>
            {errors.general}
          </div>
        )}

        {/* Debug Info */}
        <div style={{ marginBottom: '15px', fontSize: '12px', color: '#666' }}>
          <p>Debug Info:</p>
          <p>Email: "{formData.email}" (length: {formData.email.length})</p>
          <p>Password: {"*".repeat(formData.password.length)} (length: {formData.password.length})</p>
        </div>

        {/* Login Form */}
        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: '15px' }}>
            <label>Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Enter your email"
              style={{
                width: '100%',
                padding: '10px',
                border: '1px solid #ddd',
                borderRadius: '4px',
                marginTop: '5px'
              }}
              required
            />
          </div>

          <div style={{ marginBottom: '15px' }}>
            <label>Password</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Enter your password"
              style={{
                width: '100%',
                padding: '10px',
                border: '1px solid #ddd',
                borderRadius: '4px',
                marginTop: '5px'
              }}
              required
            />
          </div>

          <button 
            type="submit"
            disabled={isLoading}
            style={{
              width: '100%',
              backgroundColor: '#007bff',
              color: 'white',
              padding: '12px',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '16px'
            }}
          >
            {isLoading ? 'Signing In...' : 'Sign In'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
