import React, { useState, useEffect } from 'react';
import { getPaymentHistory, addExpense } from '../../api';
import LoadingSpinner from '../Common/LoadingSpinner';
import './Transactions.css';

const Transactions = () => {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddForm, setShowAddForm] = useState(false);
  const [filter, setFilter] = useState({
    type: 'all',
    dateRange: 'month',
    category: 'all'
  });

  const [newTransaction, setNewTransaction] = useState({
    type: 'expense',
    amount: '',
    description: '',
    category: '',
    date: new Date().toISOString().split('T')[0]
  });

  const categories = [
    'Office Supplies',
    'Events',
    'Transportation',
    'Equipment',
    'Marketing',
    'Utilities',
    'Other'
  ];

  useEffect(() => {
    fetchTransactions();
  }, [filter]);

  const fetchTransactions = async () => {
    try {
      setLoading(true);
      const response = await getPaymentHistory(filter);
      setTransactions(response.data || []);
    } catch (error) {
      console.error('Failed to fetch transactions:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAddTransaction = async (e) => {
    e.preventDefault();
    try {
      await addExpense(newTransaction);
      await fetchTransactions();
      setShowAddForm(false);
      setNewTransaction({
        type: 'expense',
        amount: '',
        description: '',
        category: '',
        date: new Date().toISOString().split('T')[0]
      });
      alert('Transaction added successfully!');
    } catch (error) {
      console.error('Failed to add transaction:', error);
      alert('Failed to add transaction. Please try again.');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTransaction(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilter(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('id-ID');
  };

  const getTotalAmount = (type) => {
    return transactions
      .filter(t => type === 'all' || t.type === type)
      .reduce((sum, t) => sum + parseFloat(t.amount || 0), 0);
  };

  if (loading) return <LoadingSpinner />;

  return (
    <div className="transactions-management">
      <div className="page-header">
        <div className="header-content">
          <h2>Transactions Management</h2>
          <p>Add income/expenses and manage financial categories</p>
        </div>
        
        <button 
          className="add-transaction-btn"
          onClick={() => setShowAddForm(!showAddForm)}
        >
          {showAddForm ? 'âœ— Cancel' : '+ Add Transaction'}
        </button>
      </div>

      {/* Financial Summary */}
      <div className="financial-summary">
        <div className="summary-card income">
          <h3>Total Income</h3>
          <p className="amount positive">Rp {getTotalAmount('income').toLocaleString()}</p>
        </div>
        <div className="summary-card expense">
          <h3>Total Expenses</h3>
          <p className="amount negative">Rp {getTotalAmount('expense').toLocaleString()}</p>
        </div>
        <div className="summary-card balance">
          <h3>Net Balance</h3>
          <p className={`amount ${(getTotalAmount('income') - getTotalAmount('expense')) >= 0 ? 'positive' : 'negative'}`}>
            Rp {(getTotalAmount('income') - getTotalAmount('expense')).toLocaleString()}
          </p>
        </div>
        <div className="summary-card total">
          <h3>Total Transactions</h3>
          <p className="count">{transactions.length}</p>
        </div>
      </div>

      {/* Add Transaction Form */}
      {showAddForm && (
        <div className="add-transaction-form">
          <h3>Add New Transaction</h3>
          <form onSubmit={handleAddTransaction}>
            <div className="form-grid">
              <div className="form-group">
                <label htmlFor="type">Transaction Type</label>
                <select
                  id="type"
                  name="type"
                  value={newTransaction.type}
                  onChange={handleInputChange}
                  required
                >
                  <option value="income">Income</option>
                  <option value="expense">Expense</option>
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="amount">Amount (Rp)</label>
                <input
                  type="number"
                  id="amount"
                  name="amount"
                  value={newTransaction.amount}
                  onChange={handleInputChange}
                  placeholder="Enter amount"
                  required
                  min="1"
                />
              </div>

              <div className="form-group">
                <label htmlFor="category">Category</label>
                <select
                  id="category"
                  name="category"
                  value={newTransaction.category}
                  onChange={handleInputChange}
                  required
                >
                  <option value="">Select Category</option>
                  {categories.map(category => (
                    <option key={category} value={category}>{category}</option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="date">Date</label>
                <input
                  type="date"
                  id="date"
                  name="date"
                  value={newTransaction.date}
                  onChange={handleInputChange}
                  required
                />
              </div>

              <div className="form-group full-width">
                <label htmlFor="description">Description</label>
                <textarea
                  id="description"
                  name="description"
                  value={newTransaction.description}
                  onChange={handleInputChange}
                  placeholder="Enter transaction description"
                  rows="3"
                  required
                />
              </div>
            </div>

            <div className="form-actions">
              <button type="submit" className="submit-btn">
                Add Transaction
              </button>
              <button 
                type="button" 
                className="cancel-btn"
                onClick={() => setShowAddForm(false)}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Filters */}
      <div className="transactions-filters">
        <div className="filter-group">
          <label htmlFor="typeFilter">Type:</label>
          <select
            id="typeFilter"
            name="type"
            value={filter.type}
            onChange={handleFilterChange}
          >
            <option value="all">All Types</option>
            <option value="income">Income Only</option>
            <option value="expense">Expense Only</option>
          </select>
        </div>

        <div className="filter-group">
          <label htmlFor="dateRangeFilter">Date Range:</label>
          <select
            id="dateRangeFilter"
            name="dateRange"
            value={filter.dateRange}
            onChange={handleFilterChange}
          >
            <option value="week">This Week</option>
            <option value="month">This Month</option>
            <option value="quarter">This Quarter</option>
            <option value="year">This Year</option>
          </select>
        </div>

        <div className="filter-group">
          <label htmlFor="categoryFilter">Category:</label>
          <select
            id="categoryFilter"
            name="category"
            value={filter.category}
            onChange={handleFilterChange}
          >
            <option value="all">All Categories</option>
            {categories.map(category => (
              <option key={category} value={category}>{category}</option>
            ))}
          </select>
        </div>

        <button className="refresh-btn" onClick={fetchTransactions}>
          ðŸ”„ Refresh
        </button>
      </div>

      {/* Transactions Table */}
      <div className="transactions-table-container">
        {transactions.length > 0 ? (
          <table className="transactions-table">
            <thead>
              <tr>
                <th>Date</th>
                <th>Description</th>
                <th>Category</th>
                <th>Type</th>
                <th>Amount</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {transactions.map((transaction) => (
                <tr key={transaction.id}>
                  <td>{formatDate(transaction.created_at || transaction.date)}</td>
                  <td>
                    <div className="transaction-description">
                      <strong>{transaction.description}</strong>
                      {transaction.member_name && (
                        <small>by {transaction.member_name}</small>
                      )}
                    </div>
                  </td>
                  <td>
                    <span className="category-tag">
                      {transaction.category || 'Member Payment'}
                    </span>
                  </td>
                  <td>
                    <span className={`type-badge ${transaction.type}`}>
                      {transaction.type}
                    </span>
                  </td>
                  <td className={`amount ${transaction.type === 'income' ? 'positive' : 'negative'}`}>
                    {transaction.type === 'income' ? '+' : '-'}Rp {parseFloat(transaction.amount || 0).toLocaleString()}
                  </td>
                  <td>
                    <span className={`status-badge ${transaction.status?.toLowerCase()}`}>
                      {transaction.status || 'Completed'}
                    </span>
                  </td>
                  <td>
                    <div className="action-buttons">
                      <button className="btn-edit">Edit</button>
                      <button className="btn-delete">Delete</button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <div className="no-data">
            <p>No transactions found for the selected filters.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Transactions;
